mapper.update:
{ CALL
  XI_SP_FMCS_ADMIN_COMMENT_U(
    #{@SEQ, 	mode=IN,	jdbcType=INTEGER},
    #{@CONTENTS,	mode=IN,	jdbcType=VARCHAR},
    #{@WRITER_NICKNM, 	mode=IN,	jdbcType=VARCHAR},
    #{@WRITER_NM, 		mode=IN,	jdbcType=VARCHAR},
    #{@ADMIN_SEQ, 		mode=IN,	jdbcType=INTEGER}
  )
}

mapper.select:
{ CALL
  XI_SP_FMCS_ADMIN_COMMENT_L(
    #{@GROUP_SEQ, 	mode=IN,	jdbcType=INTEGER},
    #{@BOARD_SEQ, 		mode=IN,	jdbcType=INTEGER}
  )
}

mapper.delete:
{ CALL
  XI_SP_FMCS_ADMIN_COMMENT_D(
    #{@SEQ, 	mode=IN,	jdbcType=INTEGER},
    #{@ADMIN_SEQ, 		mode=IN,	jdbcType=INTEGER}
  )
}

mapper.update:
{ CALL
  XI_SP_FMCS_ADMIN_COMMENT_HID_U(
    #{@SEQ, 	mode=IN,	jdbcType=INTEGER},
    #{@ADMIN_HID_YN,	mode=IN,	jdbcType=VARCHAR},
    #{@ADIMN_SEQ, 		mode=IN,	jdbcType=INTEGER}
  )
}
